import React from "react"
import { FieldArray, Field } from 'formik'

type InputProps = {
  rest: {
    id: string
    type: string
    label: string
  }
}

const InputGroup = ({ rest }) => {
  const { id, type, label } = rest


  return <div>
    <FieldArray
      name={ id }
      render={({ form: { values } }) => (
        <div>
          {values.tickets.map((ticket, index) => (
            <div key={ index }>
              <Field name={ `tickets.${index}` } />
            </div>
          ))}
        </div>
        )
      }
    />
  </div>
}

export default InputGroup
