import React from "react"
import { css } from "aphrodite/no-important"
import { FieldArray, Field } from 'formik'
import baseStyles from '../../styles/index'
import styles from './styles'

type InputGroupProps = {
  rest: {
    id: string
  }
}

const InputGroup: React.FC<InputGroupProps> = ({ rest }) => {
  const { id } = rest

  return <div className={ css(styles.inputGroupWrapper) }>
    <FieldArray
      name={ id }
      render={({ form: { values }, remove, insert }) => (
        <>
          {values.tickets.map((ticket, index) => (
            <div key={ index } className={ css(styles.field, baseStyles.flexSB) }>
              <Field name={ `tickets.${index}` } />
              <div className={ css(baseStyles.flexSB) }>
                {index === 0 && <button type='button' onClick={() => remove(index)}>-</button>}
                <button type='button' onClick={() => insert(index +1, '')}>+</button>
              </div>
            </div>
          ))}
        </>
        )
      }
    />
  </div>
}

export default InputGroup
